import {MatAutocompleteModule} from '@angular/material/autocomplete';
export interface Fruit {
  name: string;
}
@Component({
  selector: 'app-add-product',
  standalone: true,
  imports: [MatFormFieldModule, MatInputModule, AsyncPipe, MatAutocompleteModule, MatInputModule, MatIconModule, MatChipsModule, MatIconModule, MatSelectModule, NgFor, MatExpansionModule, ReactiveFormsModule],
  templateUrl: './add-product.component.html',
  styleUrl: './add-product.component.scss'
})
export class AddProductComponent {
  @ViewChild('fileInput') el!: ElementRef;
  imageUrl: any = ["../../../assts/add-img.webp","../../../assts/add-img.webp","../../../assts/add-img.webp","../../../assts/add-img.webp"];
  registrationForm!: FormGroup
  editFile: boolean = true;
  removeUpload: boolean = false;
    readonly addOnBlur = true;
  readonly separatorKeysCodes = [ENTER, COMMA] as const;
  readonly fruits = signal<any[]>([{name: 'Lemon'}, {name: 'Lime'}, {name: 'Apple'}]);
  readonly announcer = inject(LiveAnnouncer);
  productForm!: FormGroup;
    myControl = new FormControl('');
        myControl1 = new FormControl('');
  options: string[] = ['s', 'm', 'l', 'xl', 'xxl'];
  colors: string[] = ['yellow','white','pink','olive','navy','red']
  filteredOptions!: Observable<string[]>;
  filteredColors!: Observable<string[]>;
  constructor(private fb: FormBuilder, private cd: ChangeDetectorRef) {
this.productForm = this.fb.group({
      title: ['', Validators.required],
      description: [''],
      type: [''],
      brand: [''],
      collection: [[]],
      category: [''],
      price: [''],
      discount: [''],
      stock: [''],
      newProduct: [false]
    });
  }

  typeArray = [
    { key: 'Fashion', value: 'fashion' },
    { key: 'Shoes', value: 'shoes' },
    { key: 'Bags', value: 'bags' },
    { key: 'Watch', value: 'watch' },
    { key: 'Flower', value: 'flower' },
    { key: 'Pets', value: 'pets' },
    { key: 'Electronics', value: 'electronics' },
    { key: 'Vegetables', value: 'vegetables' },
    { key: 'Furniture', value: 'furniture' },
    { key: 'Beauty', value: 'beauty' },
    { key: 'Tools', value: 'tools' },
    { key: 'Gym', value: 'gym' },
    { key: 'Marijuana', value: 'marijuana' },
  ]

  brandArray = [
    { key: 'Nike', value: 'nike' },
    { key: 'Zara', value: 'zara' },
    { key: 'Denim', value: 'denim' },
    { key: 'Madame', value: 'madame' },
    { key: 'Biba', value: 'biba' },
    { key: 'Max', value: 'max' },
  ]

  collectionArray = [
    { key: 'New products', value: 'new products' },
    { key: 'Best sellers', value: 'best sellers' },
    { key: 'Featured products', value: 'featured products' },
    { key: 'On sale', value: 'on sale' },
    { key: 'New arrival', value: 'new arrival' },
    { key: 'Trending product', value: 'trending product' },
  ]
  categoryArray = [
    { key: 'Fashion', value: 'Women' },
    { key: 'Shoes', value: 'shoes' },
    { key: 'Bags', value: 'bags' },
    { key: 'Watch', value: 'watch' },
    { key: 'Flower', value: 'flower' },
    { key: 'Pets', value: 'pets' },
    { key: 'Electronics', value: 'electronics' },
    { key: 'Vegetables', value: 'vegetables' },
    { key: 'Furniture', value: 'furniture' },
    { key: 'Beauty', value: 'beauty' },
    { key: 'Tools', value: 'tools' },
    { key: 'Gym', value: 'gym' },
    { key: 'Marijuana', value: 'marijuana' },
  ]

    ngOnInit() {
    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map(value => this._filter(value || '')),
    );

        this.filteredColors = this.myControl1.valueChanges.pipe(
      startWith(''),
      map(value => this._filter1(value || '')),
    );
  }

  private _filter(value: string): string[] {
    const filterValue = value.toLowerCase();

    return this.options.filter(option => option.toLowerCase().includes(filterValue));
  }

    private _filter1(value: string): string[] {
    const filterValue = value.toLowerCase();

    return this.colors.filter(option => option.toLowerCase().includes(filterValue));
  }

  uploadFile(event: any) {
    let reader = new FileReader(); // HTML5 FileReader API
    let file = event.target.files[0];
    if (event.target.files && event.target.files[0]) {
      reader.readAsDataURL(file);

      // When file uploads set it to file formcontrol
      reader.onload = () => {
        this.imageUrl = reader.result;
        this.registrationForm.patchValue({
          file: reader.result
        });
        this.editFile = false;
        this.removeUpload = true;
      }
      // ChangeDetectorRef since file is loading outside the zone
      this.cd.markForCheck();
    }
  }


   add(event: MatChipInputEvent): void {
    const value = (event.value || '').trim();

    // Add our fruit
    if (value) {
      this.fruits.update(fruits => [...fruits, {name: value}]);
    }

    // Clear the input value
    event.chipInput!.clear();
  }

  remove(fruit: Fruit): void {
    this.fruits.update(fruits => {
      const index = fruits.indexOf(fruit);
      if (index < 0) {
        return fruits;
      }

      fruits.splice(index, 1);
      this.announcer.announce(`Removed ${fruit.name}`);
      return [...fruits];
    });
  }

  edit(fruit: Fruit, event: MatChipEditedEvent) {
    const value = event.value.trim();

    // Remove fruit if it no longer has a name
    if (!value) {
      this.remove(fruit);
      return;
    }

    // Edit existing fruit
    this.fruits.update(fruits => {
      const index = fruits.indexOf(fruit);
      if (index >= 0) {
        fruits[index].name = value;
        return [...fruits];
      }
      return fruits;
    });
  }


  jsonTest(){
    
  }
}
